function debug(message, obj) { console.info("CREATE_PAYMENT: " + message + " => ", obj); }

//As the user is interacting with the payment app, it is broadcasting messages to the page. This function listens for those messages.
function receivePaymentAppMessage(event) {

    debug("Payment App Post Message", event);

    //event.data.Action holds the type of action the payment app has taken.
    var eventType = (event.data.hasOwnProperty("Action")) ? event.data.Action : null;

    if(eventType == "notifyPaymentCompleted") {

        //Check to see if the payment received checkbox has already been checked or not. If it has, we don't want to override the original payment date.
        if(oppRecord.Payment_Received__c == "false") {
            var opp = new sforce.SObject("Opportunity");
            opp.Id = opportunityId;
            opp.Payment_Received__c = true;
            opp.Payment_Received_Date__c = new Date();
            var result = sforce.connection.update([opp]);
            debug("opp udate result", result);
        }
    }
}

//Simple function to query out a few fields from the current opportunity.
function getOpportunityRecord(oppId) {
    var query = sforce.connection.query("SELECT Payment_Received__c, Payment_Received_Date__c, AccountId, Recurring_Monthly_Revenue_RMR__c FROM Opportunity WHERE Id = '" + oppId + "'");
    debug("opportunity query", query);

    var records = query.getArray("records");
    debug("convert to array", records);

    var opp = (records.length > 0) ? records[0] : null;
    debug("opportunity record", opp);

    return opp;
}

function getAccountRecord(acctId) {
    var query = sforce.connection.query("SELECT Lock_Account__c FROM Account WHERE Id = '" + acctId + "'");
    debug("account query", query);

    var records = query.getArray("records");
    debug("convert to array", records);

    var acct = (records.length > 0) ? records[0] : null;
    debug("account record", acct);

    return acct;
}

