var j$ = jQuery.noConflict();
paymentApp.jQuery = j$;
var paymentEventListenerInitialized = false;
var monitorPaymentFrameInterval;
var currentPaymentFrame;

j$(document).ready(function() {
    if(sforce.console.isInConsole()) {
      // Set up the OE Tool Tab
      sforce.console.getFocusedSubtabId(function(result) {
        var currentSubTabId = result.id;
        console.log('[Order Entry Tool] Current Subtab Id: ' + currentSubTabId);
        var url = '{!$Setup.ConnectionSettings__c.OE_Tool__c}/search?opportunityId={!Opportunity.Id}';
        if({!$Permission.OE_Tool_Pilot}) {
            url = '{!$Setup.ConnectionSettings__c.OE_Tool_Pilot__c}/search?opportunityId={!Opportunity.Id}';
        }
        if({!$Permission.OE_Tool_Alt}) {
            url = '{!$Setup.ConnectionSettings__c.OE_Tool_Alt__c}/search?opportunityId={!Opportunity.Id}';
        }
        // Launch the OE Tool in a new subtab
        sforce.console.openSubtab(sforce.console.getEnclosingPrimaryTabId().id, url, true, 'Order Entry Tool', null, function(result) {
          var OEToolSubTabId = result.id;
          console.log('[Order Entry Tool] OE Tool Frame Id: ' + OEToolSubTabId);
          if(!OEToolSubTabId) {
              alert('This console needs to be whitelisted to use the OE Tool');
          }
          sforce.console.setTabIcon('https://media.vivint.com/www.vivint.com/en/images/global/vivint-16x16.png', OEToolSubTabId, function(result) {

          });
          // When related tab is closed or page is navigated away, close the OE Tab so that it isn't reopened automatically.
          // Event listeners will not be wired up unless the OE Tool button is clicked.
          sforce.console.addEventListener(sforce.console.ConsoleEvent.CLOSE_TAB, function(result) {
            console.log('[Order Entry Tool] Lead/Opportunity Tab Closed, Closing Linked OE Tool Tab');
            sforce.console.closeTab(OEToolSubTabId);
          }, { tabId: currentSubTabId });
          window.onbeforeunload = function(){
            console.log('[Order Entry Tool] Page Unloading, Closing Open OE Tool');
            sforce.console.closeTab(OEToolSubTabId);
          };
          /* Set up Payment App event listeners for Five9 CTI Integration */
          paymentApp.container.window = top.window;
          paymentApp.container.document = top.window.document;
          var monitorPaymentFrame = function() {
            if(j$('#' + OEToolSubTabId + ' iframe', j$(paymentApp.container.document)).length > 0) {
                if(j$('#' + OEToolSubTabId + ' iframe', j$(paymentApp.container.document))[0].contentWindow.frames.length > 0) {
                    if(j$('#' + OEToolSubTabId + ' iframe', j$(paymentApp.container.document))[0].contentWindow.frames[0] != currentPaymentFrame) {
                        console.log('[Payment CTI] Found Payment Frame');
                        paymentApp.frame = j$('#' + OEToolSubTabId + ' iframe', j$(paymentApp.container.document))[0].contentWindow.frames[0];
                        currentPaymentFrame = j$('#' + OEToolSubTabId + ' iframe', j$(paymentApp.container.document))[0].contentWindow.frames[0];

                        if(!paymentEventListenerInitialized) {
                        paymentApp.setUpEventListener();
                            console.log('[Payment CTI] Event Listener Loaded');
                            paymentEventListenerInitialized = true;
                        }
                    }
                }
            }
          };
          monitorPaymentFrameInterval = setInterval(monitorPaymentFrame, 1000);
        }, ['oetool']);
      });
    }else{
      document.location.href='/apex/Opportunity_OE_Tool_NIS?Id={!Opportunity.Id}';
    }
});

