        window.top.location.href = url;
    }
}

function isQuoteValid() {
    var queryQuote = sforce.connection.query("SELECT Id FROM zqu__Quote__c WHERE zqu__Opportunity__c = '{!Opportunity.Id}'");
    var records = queryQuote.getArray("records");

    var quoteIds = [];
    for(var i = 0; i < records.length; ++i) { quoteIds.push("'" + records[i].Id + "'"); }
    var queryQuoteCharges = sforce.connection.query("SELECT Id, External_Transaction_ID__c FROM zqu__QuoteCharge__c where zqu__Quote__c in (" + quoteIds.join(',') + ")");
    records = queryQuoteCharges.getArray("records");

    var isValid = true;
    for(var i = 0; i < records.length; ++i) {
        if(records[i].External_Transaction_ID__c == null || records[i].External_Transaction_ID__c == '') {
            isValid = false;
            break;
        }
    }
    return isValid;
}

